import React from 'react'
import {BiMobile, BsGeoAlt, HiOutlineMailOpen} from "react-icons/all";
import '../stylesheets/contactUs.scss'

class ContactUs extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            email: '',
            message: ''
        }
    }

    onNameChange = event => {
        this.setState({name: event.target.value})
    }

    onEmailChange = event => {
        this.setState({email: event.target.value})
    }

    onMessageChange = event => {
        this.setState({message: event.target.value})
    }

    handleSubmit = event => {
        event.preventDefault();
        console.log(this.state);
        this.resetForm()
    }

    resetForm = () => {
        this.setState({
            name: '',
            email: '',
            message: ''
        })
    }

    render() {
        return (
            <div className="contact-us">
                <div className="row">
                    <div className="col-lg-4 col-sm-6 col-md-4">
                        <BiMobile size={42}/>
                        <p>+91-731-4291119</p>
                    </div>
                    <div className="col-lg-4 col-sm-6 col-md-4">
                        <HiOutlineMailOpen size={42}/>
                        <p>contact@nairandassociates.in</p>
                    </div>
                    <div className="col-lg-4 col-sm-6 col-md-4">
                        <BsGeoAlt size={42}/>
                        <p>Yeshwant Plaza, S-118, Indore, Madhya Pradesh 452001</p>
                    </div>
                </div>
                <div className="row">
                    <div className="col-lg-6 col-sm-12 col-md-6">
                        <form id="contact-form" onSubmit={this.handleSubmit.bind(this)} method="POST">
                            <div className="form-group">
                                <label htmlFor="name">Name</label>
                                <input type="text" className="form-control" value={this.state.name} onChange={this.onNameChange.bind(this)} />
                            </div>
                            <div className="form-group">
                                <label htmlFor="exampleInputEmail1">Email address</label>
                                <input type="email" className="form-control" aria-describedby="emailHelp" value={this.state.email} onChange={this.onEmailChange.bind(this)} />
                            </div>
                            <div className="form-group">
                                <label htmlFor="message">Message</label>
                                <textarea className="form-control" rows="5" value={this.state.message} onChange={this.onMessageChange.bind(this)} />
                            </div>
                            <button type="submit" className="btn btn-primary">Submit</button>
                        </form>
                    </div>
                    <div className="col-lg-6 col-sm-12 col-md-6">
                        map
                    </div>
                </div>
            </div>
        )
    }
}

export default ContactUs
