import React from 'react';
import './style.scss';
import {FcAbout, SiAboutDotMe, FaReadme, RiTeamFill, GrGallery, FaUsers} from "react-icons/all";

class Services extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            services: ['About Us', 'Areas of Practice', 'Our Team', 'Gallery', 'Our Clientele', 'Research & Publications', 'Our Achievements', 'Outstation Services', 'Other Services', 'Contact Us']
        }
    }

    getIcon(iconCategory) {
        switch(iconCategory) {
            case "About Us": return <FcAbout size={56} />
            case "Areas of Practice": return <FaReadme size={56} />
            case "Our Team": return <RiTeamFill size={56} />
            case "Gallery": return <GrGallery size={56} />
            case "Our Clientele": return <FaUsers size={56} />
            case "Research & Publications": return <FcAbout size={56} />
            case "Our Achievements": return <FcAbout size={56} />
            case "Outstation Services": return <FcAbout size={56} />
            case "Other Services": return <FcAbout size={56} />
            case "Contact Us": return <FcAbout size={56} />
            default: return <SiAboutDotMe size={56} />
        }
    }

    render() {
        return (
            <section id="services">
                <div className="row">
                    {
                        this.state.services.map(service => (
                            <div className="col-sm-3">
                                <div className="card">
                                    <div className="card-body">
                                        {this.getIcon(service)}
                                        <h3 className="card-title">{ service }</h3>
                                    </div>
                                </div>
                            </div>
                        ))
                    }
                </div>
            </section>

        )
    }
}

export default Services;
